import React, { Fragment, useEffect, useState } from "react";
import MetaData from "../layouts/MetaData";

const EditBooks = () => {
  const [booksByCategory, setBooksByCategory] = useState({});
  const [loading, setLoading] = useState(true);
  const [newBook, setNewBook] = useState({
    title: "",
    author: "",
    file: null,
  });

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch("/api/v1/products");
        const data = await response.json();

        if (data.success) {
          setBooksByCategory(data.booksByCategory || {});
        }
      } catch (error) {
        console.error("Error fetching books:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    setNewBook((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleAddBook = (e) => {
    e.preventDefault();
    console.log("Add Book Payload:", newBook);

    // TODO: Implement actual API call to upload book
    alert("Book added (mock only)");
  };

  const categoryTitles = {
    NDA: "National Defence Academy",
    CDS: "Combined Defence Services",
    SSB: "Services Selection Board",
  };

  return (
    <Fragment>
      <MetaData title="Edit Books" />
      <div className="head-exam">
        <h1>Edit Books</h1>
      </div>

      <div>
        {loading ? (
          <p>Loading books...</p>
        ) : Object.keys(booksByCategory).length > 0 ? (
          Object.entries(booksByCategory).map(([category, books]) => (
            <div key={category} className="books-exam books-all">
              <div className="h3-exam h3-all">
                {categoryTitles[category] || category}
              </div>
              <div className="source-exam">
                {books.map((book) => (
                  <div className="b1-exam" key={book._id}>
                    <img
                      src={book.images?.[0]?.url || "/default.jpg"}
                      alt={book.title}
                    />
                    <p>{book.title}</p>
                    <button className="btn btn-secondary btn-sm">Edit</button>
                  </div>
                ))}
              </div>
              <p className="scroll">
                Scroll for more <img src="/images/more.svg" alt="more" />
              </p>
            </div>
          ))
        ) : (
          <p>No books available.</p>
        )}
      </div>

      <div className="add-book mt-5">
        <h2>Add New Book</h2>
        <form onSubmit={handleAddBook} className="d-flex flex-column gap-3">
          <input
            type="text"
            placeholder="Book Title"
            name="title"
            value={newBook.title}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            placeholder="Author"
            name="author"
            value={newBook.author}
            onChange={handleInputChange}
            required
          />
          <input
            type="file"
            accept="image/*"
            name="file"
            onChange={handleInputChange}
            required
          />
          <button type="submit" className="btn btn-primary">
            Add Book
          </button>
        </form>
      </div>
    </Fragment>
  );
};

export default EditBooks;
