import {
    ALL_USER_REQUEST,
    ALL_USER_SUCCESS,
    ALL_USER_FAIL
  } from "../constants/adminConstants";
  
  import { CLEAR_ERRORS } from "../constants/userConstants";
  
  const initialState = {
    users: [],
    loading: false,
    error: null,
  };
  
  export const adminReducer = (state = initialState, action) => {
    switch (action.type) {
      case ALL_USER_REQUEST:
        return {
          ...state,
          loading: true,
          users: [],
        };
  
      case ALL_USER_SUCCESS:
        return {
          ...state,
          loading: false,
          users: action.payload,
        };
  
      case ALL_USER_FAIL:
        return {
          ...state,
          loading: false,
          error: action.payload,
        };
  
      case CLEAR_ERRORS:
        return {
          ...state,
          error: null,
        };
  
      default:
        return state;
    }
  };
