import React, { Fragment, useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProducts } from "../../actions/productActions"; // Adjust the path if needed

const EditBooks = () => {
  const dispatch = useDispatch();
  const { products, loading } = useSelector((state) => state.products); // Ensure this reducer exists
  const [newBook, setNewBook] = useState({
    title: "",
    author: "",
    file: null,
  });

  useEffect(() => {
    dispatch(getProducts());
  }, [dispatch]);

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    setNewBook((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleAddBook = (e) => {
    e.preventDefault();
    console.log("Add Book Payload:", newBook);

    // TODO: Dispatch action to send new book to backend
    alert("Book added (mock only)");
  };

  return (
    <Fragment>
      <div className="d-flex flex-wrap gap-3 mb-4">
        {loading ? (
          <p>Loading books...</p>
        ) : (
          products.map((book) => (
            <div className="card" style={{ width: "18rem" }} key={book._id}>
              <img
                src={book.images?.[0]?.url || "/default.jpg"}
                className="card-img-top"
                alt={book.title}
              />
              <div className="card-body">
                <h5 className="card-title">{book.title}</h5>
                <p className="card-text">{book.category}</p>
                <button type="button" className="btn btn-secondary">
                  Edit
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      <div className="add-book mt-5">
        <h2>Add New Book</h2>
        <form onSubmit={handleAddBook} className="d-flex flex-column gap-3">
          <input
            type="text"
            placeholder="Book Title"
            name="title"
            value={newBook.title}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            placeholder="Author"
            name="author"
            value={newBook.author}
            onChange={handleInputChange}
            required
          />
          <input
            type="file"
            accept="image/*"
            name="file"
            onChange={handleInputChange}
            required
          />
          <button type="submit" className="btn btn-primary">
            Add Book
          </button>
        </form>
      </div>
    </Fragment>
  );
};

export default EditBooks;
