import React, {
  useEffect,
  useState,
  Fragment,
  useCallback,
  useRef,
} from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { getTests } from "../actions/productActions";

const Test = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { exam, id } = useParams();

  const { tests, error } = useSelector((state) => state.tests);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [currentTest, setCurrentTest] = useState(null);
  const [timeLeft, setTimeLeft] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  const pauseModalRef = useRef(null);
  const pauseModalInstanceRef = useRef(null);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (pauseModalRef.current && window.bootstrap?.Modal) {
        pauseModalInstanceRef.current = new window.bootstrap.Modal(pauseModalRef.current, {
          backdrop: 'static', // Optional: prevent closing by clicking outside
          keyboard: false     // Optional: prevent ESC key closing
        });
      }
    }, 200); // Slight delay ensures modal is in the DOM
    return () => clearTimeout(timer);
  }, []);
  

  const handleSubmit = useCallback(() => {
    if (!currentTest || !currentTest.questions) return;

    let score = 0;
    currentTest.questions.forEach((q, idx) => {
      if (answers[idx] === q.answer) {
        score += currentTest.marksCorrect;
      } else if (answers[idx]) {
        score -= currentTest.marksWrong;
      }
    });

    alert(`Test submitted! Your score: ${score}`);
    navigate("/");
  }, [answers, currentTest, navigate]);

  useEffect(() => {
    if (exam) {
      dispatch(getTests(exam));
    }
  }, [dispatch, exam]);

  useEffect(() => {
    const test = tests.find((t) => t._id === id);
    if (test) {
      setCurrentTest(test);
      setTimeLeft(test.timeDuration * 60);
    }
  }, [tests, id]);

  useEffect(() => {
    if (timeLeft <= 0) {
      handleSubmit();
      return;
    }

    if (isPaused) return;

    const timer = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft, isPaused, handleSubmit]);

  const formatTime = (seconds) => {
    const h = String(Math.floor(seconds / 3600)).padStart(2, "0");
    const m = String(Math.floor((seconds % 3600) / 60)).padStart(2, "0");
    const s = String(seconds % 60).padStart(2, "0");
    return `${h}:${m}:${s}`;
  };

  const handleOptionChange = (e) => {
    setAnswers({
      ...answers,
      [currentQuestionIndex]: e.target.value,
    });
  };

  const handleNext = () => {
    if (!currentTest || !currentTest.questions) return;
    if (currentQuestionIndex < currentTest.questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handlePrevious = () => {
    if (!currentTest || !currentTest.questions) return;
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  const toggleFullScreen = () => {
    const elem = document.documentElement;
    if (!document.fullscreenElement) {
      elem.requestFullscreen().catch((err) => console.error(err));
    } else {
      document.exitFullscreen();
    }
  };

  if (error) {
    return <p style={{ color: "red" }}>‚ùå Failed to load test: {error}</p>;
  }

  if (
    !currentTest ||
    !currentTest.questions ||
    currentTest.questions.length === 0
  ) {
    return <p>Loading test...</p>;
  }

  const question = currentTest.questions[currentQuestionIndex];
  const selected = answers[currentQuestionIndex] || "";

  return (
    <Fragment>
      <div className="test-body">
        {isPaused && (
          <div className="resume-container">
            <div className="resume-content">
              <p className="resume-text">
                You have paused the test, resume your test to keep going!!!
              </p>
              <button className="resume-btn" onClick={() => setIsPaused(false)}>
                Resume
              </button>
            </div>
          </div>
        )}

        <div className="test-head">
          <div className="test-name">
            <p>{currentTest.title}</p>
          </div>
          <div className="timeleft">
            <p>Time left {formatTime(timeLeft)}</p>
          </div>
          <div className="full-button">
            <button
              className="pause-btn"
              onClick={(e) => {
                e.preventDefault();
                pauseModalInstanceRef.current?.show();
              }}
            >
              Pause
            </button>

            <button
              className="full-btn"
              onClick={toggleFullScreen}
            >
              Enter Full Screen
            </button>
          </div>
        </div>

        <div className="test-content">
          <div className="test-section">
            <div className="ques">
              <p>Question No. {currentQuestionIndex + 1}</p>
            </div>
            <div className="section-marks">
              <div className="correct">
                <p>Correct</p>
                <p>+{currentTest.marksCorrect}</p>
              </div>
              <div className="incorrect">
                <p>Incorrect</p>
                <p>-{currentTest.marksWrong}</p>
              </div>
            </div>
          </div>

          <div className="test-question">
            <div className="question">
              <p>{question.questionText}</p>
            </div>
            <div className="test-options">
              {question.options.map((option, idx) => (
                <div key={idx}>
                  <label>
                    <input
                      type="radio"
                      name={`question-${currentQuestionIndex}`}
                      value={option}
                      checked={selected === option}
                      onChange={handleOptionChange}
                    />
                    <span>{option}</span>
                  </label>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="test-submit">
          <div className="test-save">
            <button
              onClick={handlePrevious}
              disabled={currentQuestionIndex === 0}
            >
              Previous Question
            </button>
            <button
              onClick={handleNext}
              disabled={
                currentQuestionIndex === currentTest.questions.length - 1
              }
            >
              Save & Next
            </button>
          </div>
          <div className="test-save">
            <button
              className="submit-btn btn btn-success"
              onClick={handleSubmit}
            >
              Submit
            </button>
          </div>
        </div>
      </div>

      {/* Pause Confirmation Modal */}
      <div
        className="modal fade"
        id="pauseConfirmModal"
        tabIndex="-1"
        aria-labelledby="pauseConfirmModalLabel"
        aria-hidden="true"
        ref={pauseModalRef}
      >
        <div className="modal-dialog modal-dialog-centered">
          <div className="modal-content">
            <div className="modal-header bg-dark text-dark">
              <h5 className="modal-title text-white" id="pauseConfirmModalLabel">
                Pause Test?
              </h5>
              <button
                type="button"
                className="btn-close text-white"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div className="modal-body">
              Are you sure you want to pause the test? Timer will stop and
              questions will be hidden.
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="full-btn"
                data-bs-dismiss="modal"
              >
                No
              </button>
              <button
                type="button"
                className="pause-btn"
                onClick={() => {
                  setIsPaused(true);
                  pauseModalInstanceRef.current?.hide();
                }}
              >
                Yes, Pause
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default Test;
