import {
    ALL_USER_REQUEST,
    ALL_USER_SUCCESS,
    ALL_USER_FAIL,
    UPDATE_USER_ROLE_SUCCESS
  } from "../constants/adminConstants";
  
  import { CLEAR_ERRORS } from "../constants/userConstants";
  
  const initialState = {
    users: [],
    loading: false,
    error: null,
  };
  
  export const adminReducer = (state = initialState, action) => {
    switch (action.type) {
      case ALL_USER_REQUEST:
        return {
          ...state,
          loading: true,
          users: [],
        };
  
        case ALL_USER_SUCCESS:
            return {
              ...state,
              loading: false,
              users: action.payload.users, // ✅ Just the array
            };
          
  
      case ALL_USER_FAIL:
        return {
          ...state,
          loading: false,
          error: action.payload,
        };
  

        case UPDATE_USER_ROLE_SUCCESS:
  return {
    ...state,
    loading: false,
    users: state.users.map((user) =>
      user._id === action.payload._id ? action.payload : user
    ),
  };

      case CLEAR_ERRORS:
        return {
          ...state,
          error: null,
        };
  
      default:
        return state;
    }
  };
