import React, { Fragment } from "react";
import { useLocation, useParams } from "react-router-dom";

const MockList = () => {
  const location = useLocation();
  const { exam, mockTest } = useParams();

  // Fallbacks in case user refreshed and state is lost
  const selectedExam = location.state?.selectedExam || exam;
  const selectedMockTest = location.state?.selectedMockTest || mockTest;

  return (
    <Fragment>
      <h1>
        {selectedMockTest === "DPP" || selectedMockTest.toLowerCase() === "dpp"
          ? `${selectedExam} - Daily Practice Papers`
          : `${selectedExam} - Full Mock Tests`}
      </h1>

      {/* Render content conditionally */}
      {selectedMockTest.toLowerCase() === "dpp" ? (
        <div className="mock-container">
          {/* Your logic for listing DPP tests */}
          <p>Here are your DPPs for {selectedExam}</p>
        </div>
      ) : (
        <div className="mock-container">
          {/* Your logic for listing Full Mock Tests */}
          <p>Here are your Full Mock Tests for {selectedExam}</p>
        </div>
      )}
    </Fragment>
  );
};

export default MockList;
