import {
  TEST_REQUEST,
  TEST_SUCCESS,
  TEST_FAIL,
  CLEAR_ERRORS,
} from "../constants/productConstants";

export const testReducer = (state = { loading: false, tests: [], error: null }, action) => {
  
    switch (action.type) {
      case TEST_REQUEST:
        return { ...state, loading: true };
  
      case TEST_SUCCESS:
        return { ...state, loading: false, tests: action.payload, error: null };
  
      case TEST_FAIL:
        return { ...state, loading: false, error: action.payload };
  
      case CLEAR_ERRORS:
        return { ...state, error: null };
  
      default:
        return state;
    }
  };
